import MenuItem from '@material-ui/core/MenuItem';
import blue from '@material-ui/core/colors/blue';
import red from '@material-ui/core/colors/red';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';
import MaskedInput from 'react-text-mask';
import Typography from '@material-ui/core/Typography';
import { unstable_Box as Box } from '@material-ui/core/Box';
import TextField from '@material-ui/core/TextField';
import DataTable from './DataTable'
import Recurso from './Recurso'
import clsx from 'clsx';
import Icon from '@material-ui/core/Icon';

import InputAdornment from '@material-ui/core/InputAdornment';

import { makeStyles } from '@material-ui/core/styles';
const categorias = [
  {
    value: '',
    label: '',
  },
  {
    value: 'USD',
    label: 'Equipamento',
  },
  {
    value: 'EUR',
    label: 'Espaço',
  },
  {
    value: 'EUR',
    label: 'Móvel',
  },
];
const setores = [
  {
    value: '',
    label: '',
  },
  {
    value: 'USD',
    label: 'Setor 01',
  },
  {
    value: 'EUR',
    label: 'Setor 02',
  },
];

export default function TextFields(props) {
  const [values, setValues] = React.useState({
    name: 'Cat in the Hat',
    age: '',
    multiline: 'Controlled',
    tipos: '',
    setores: '',
  });

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };
    return (
      <div>
      <Typography component="div">
      <Box fontWeight={500} textAlign='center' fontSize={34} fontFamily="Roboto" style={{color:'#025c'}} m={2}>
        {props.h1first}
      </Box>
      <Box fontWeight={500} textAlign='right' fontSize={15} fontFamily="Roboto" style={{color:'#025c'}} m={2}>
        Funcionário : {props.numero}
      </Box>
      <TextField
        id="outlined-select-currency"
        select
        label="Categoria"
        value={this.state.categorias}
        onChange={this.handleChange('categorias')}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Icon style={{color:'black'}}>
        search
      </Icon>
            </InputAdornment>
          ),
        }}
        helperText="Os Recursos são organizados em ordem alfabética"
        margin="normal"
        variant="outlined"
      >
        {categorias.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        id="outlined-select-currency"
        select
        label="Setor"
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <Icon style={{color:'black'}}>
        search
      </Icon>
            </InputAdornment>
          ),
        }}
        value={this.state.setores}
        style={{minWidth:120}}
        onChange={this.handleChange('setores')}
        margin="normal"
        variant="outlined"
      >
        {setores.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
